mapper.select:

    	SELECT cartID,userID,CART.bbsID,amount,title,price
    	FROM SHOPPING.CART, SHOPPING.BBS
    	WHERE 	CART.bbsID = BBS.bbsID
    	AND		userID = #{userID}
		ORDER BY CART.bbsID DESC
		LIMIT #{displayPost}, #{postNum}


SELECT COUNT(*)
FROM SHOPPING.CART
WHERE 	userID = #{userID}


	SELECT *
	FROM SHOPPING.CART
    WHERE	bbsID = #{bbsID}    	
	AND		userID = #{userID}


SELECT cartID
FROM SHOPPING.CART
ORDER BY cartID DESC;

mapper.delete:

    	UPDATE SHOPPING.CART
    	SET amount = #{amount}
		WHERE 	bbsID = #{bbsID}
		AND		userID = #{userID}


    	UPDATE SHOPPING.CART
    	SET isOrder = 1
		WHERE 	bbsID = #{bbsID}
		AND		userID = #{userID}
