mapper.select:

    	SELECT COUNT(*)
    	FROM SHOPPING.ITEMORDER
		WHERE userID = #{userID}


SELECT COUNT(*)
FROM SHOPPING.ORDERITEMS
WHERE orderID = #{orderID}

mapper.delete:

    	UPDATE SHOPPING.BBS
    	SET sell = sell + #{amount}
		WHERE bbsID = #{bbsID}


    	UPDATE SHOPPING.BBS
    	SET sell = sell - #{amount}
		WHERE bbsID = #{bbsID}

mapper.select:

SELECT orderID
FROM SHOPPING.ITEMORDER
ORDER BY orderID DESC;

mapper.delete:

    	UPDATE SHOPPING.ITEMORDER
    	SET delivery = #{delivery}
		WHERE orderID = #{orderID}
